mapper.select:
SELECT * FROM ( select a.*,FORMAT(a.wdate, 'yyyy.MM.dd') AS reg_date, ROW_NUMBER() OVER (ORDER BY a.num) AS rownum, (SELECT COUNT(*) FROM KCC_EGIS.egis.TBL_NEWS_TAIL b WHERE b.info_num = a.num) AS tail_count from KCC_EGIS.egis.TBL_NEWS a where 1=1 and a.part = 'notice' and a.flag = 'N'
and (a.subject like CONCAT('%', #{keyWord}, '%') or a.content like CONCAT('%', #{keyWord}, '%'))
) A ORDER BY num desc OFFSET (#{currentPage}-1)*#{limit} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT * FROM ( select A.* , FORMAT(A.wdate, 'yyyy.MM.dd') AS reg_date , KCC_EGIS.egis.FN_MASK_NAME(writer) writer2 , ROW_NUMBER() OVER (ORDER BY A.num) AS rownum , (SELECT COUNT(*) FROM KCC_EGIS.egis.TBL_FREE_TAIL b WHERE b.info_num = A.num) AS tail_count , ROW_NUMBER() OVER (PARTITION BY A.SUBJECT ORDER BY A.SUBJECT) AS SEQ from ( select a.* from KCC_EGIS.egis.TBL_FREE a where 1=2 and a.chk_del = 'N'
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and a.subject LIKE CONCAT('%', #{keyWord}, '%')
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and CONTAINS(a.content, #{keyWord})
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and a.writer LIKE CONCAT('%', REPLACE(#{keyWord},'*','%'), '%')
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and a.num in (select distinct info_num FROM KCC_EGIS.egis.TBL_FREE_TAIL where CONTAINS(content, #{keyWord}))
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and a.num in (select distinct info_num FROM KCC_EGIS.egis.TBL_FREE_TAIL where writer LIKE CONCAT('%', REPLACE(#{keyWord},'*','%'), '%'))
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
) A ) A WHERE A.SEQ = 1 ORDER BY num desc OFFSET (#{currentPage}-1)*#{limit} ROWS FETCH NEXT #{limit} ROWS ONLY

select count(*) from ( SELECT * FROM ( select A.* , FORMAT(A.wdate, 'yyyy.MM.dd') AS reg_date , KCC_EGIS.egis.FN_MASK_NAME(writer) writer2 , ROW_NUMBER() OVER (ORDER BY A.num) AS rownum , (SELECT COUNT(*) FROM KCC_EGIS.egis.TBL_FREE_TAIL b WHERE b.info_num = A.num) AS tail_count , ROW_NUMBER() OVER (PARTITION BY A.SUBJECT ORDER BY A.SUBJECT) AS SEQ from ( select a.* from KCC_EGIS.egis.TBL_FREE a where 1=2 and a.chk_del = 'N'
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and a.subject LIKE CONCAT('%', #{keyWord}, '%')
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and CONTAINS(a.content, #{keyWord})
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and a.writer LIKE CONCAT('%', REPLACE(#{keyWord},'*','%'), '%')
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and a.num in (select distinct info_num FROM KCC_EGIS.egis.TBL_FREE_TAIL where CONTAINS(content, #{keyWord}))
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
and a.num in (select distinct info_num FROM KCC_EGIS.egis.TBL_FREE_TAIL where writer LIKE CONCAT('%', REPLACE(#{keyWord},'*','%'), '%'))
union all select a.* from KCC_EGIS.egis.TBL_FREE a where 1=1 and a.chk_del = 'N'
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
) A ) A WHERE A.SEQ = 1 ) A

select count(*) from ( select a.*,FORMAT(a.wdate, 'yyyy.MM.dd') AS reg_date from KCC_EGIS.egis.TBL_NEWS a where 1=1 and a.part = 'notice' and a.flag = 'N'
and (a.subject like CONCAT('%', #{keyWord}, '%') or a.content like CONCAT('%', #{keyWord}, '%'))
) A

select count(*) from (
	select *,FORMAT(wdate , 'yyyy.MM.dd') AS formatted_date,
    FORMAT(wdate, 'HH:mm:ss') AS formatted_time
    from KCC_EGIS.egis.TBL_FREE_TAIL
	where info_num = #{num}
) A

select count(*) from (
	select * from KCC_EGIS.egis.TBL_WALLPAPER
) A

mapper.insert:
insert into KCC_EGIS.egis.TBL_FREE_TAIL (
	 info_num, id, writer, content, ip, wdate  
)values(
	 #{num}, #{id}, #{name}, #{content}, #{ip}, getdate()
)
