mapper.select:
select count(*) from kcc2022_esoom.egis.TBL_MEMBER 
where 1=1 

	and id = #{member_id}


	and email = #{email}


	and num != #{num}

select * from kcc2022_esoom.egis.TBL_MEMBER 
where 1=1

	and id = #{member_id}


	and num = #{num}

and chk_state = 'S'

select *,kcc2022_esoom.egis.FN_MASK_ID(id) as id2
 from kcc2022_esoom.egis.TBL_MEMBER 
where 1=1
and name = #{name}
and jumin = #{jumin}
and email = #{email}
and chk_state = 'S'

select *,kcc2022_esoom.egis.FN_MASK_ID(id) as id2
 from kcc2022_esoom.egis.TBL_MEMBER 
where 1=1
and name = #{name}
and id = #{member_id}
and email = #{email}
and chk_state = 'S'

mapper.insert:
insert into kcc2022_esoom.egis.TBL_MEMBER (
	 id, sha_pwd, name, jumin, zipcode, addr, daddr, email, tel
	,chk_email, chk_sms ,chk_grade , chk_state ,reg_date ,login_date,logindate,out_date
	,pwd_date,ip,sex,di,player_no
)values(
	 #{member_id}, #{pwd}, #{name}, #{jumin}, #{zipcode}, #{addr}, #{daddr}, #{email}, #{tel}
	,#{chk_email}, #{chk_sms} , '10', 'S', getdate(), CONVERT(VARCHAR(10), GETDATE(), 120),getdate(),'1900-01-01 00:00:00'
	,CONVERT(VARCHAR(10), GETDATE(), 120),#{ip},#{sex},#{di},#{player_no}
)

mapper.update:
update kcc2022_esoom.egis.TBL_MEMBER
set player_no = #{player_no}
where
num = #{num}

update kcc2022_esoom.egis.TBL_MEMBER
set tel = #{tel}
	,jumin = #{jumin}
	,email = #{email}
	,zipcode = #{zipcode}
	,addr = #{addr}
	,daddr = #{daddr}
	,player_no = #{player_no}
	,di = #{di}
	,name = #{name}
where
num = #{num}

update kcc2022_esoom.egis.TBL_MEMBER
set chk_state = 'N'
   ,out_date = GETDATE()
   ,delete_reason = #{delete_reason}
where
num = #{num}
