mapper.select:
select t1.season_code, t1.game_no, t1.game_code,t1.game_round, t1.away_team, t1.home_team, t1.game_date, isnull(t3.play_min, 0) play_min, t2.stadium_name_2, isnull(t3.score, 0) away_score, isnull(t4.score, 0) home_score, isnull(t5.game_end, '') game_end, t1.game_start, t1.week_day, t6.tv_play, t7.team_name_1 AS home_team_name,t8.team_name_1 AS away_team_name from KCC_EGIS.kbl_data.team_schedule t1 inner join KCC_EGIS.kbl_data.code_stadium t2 on t1.stadium_code=t2.stadium_code and t1.season_code=t2.season_code inner join KCC_EGIS.kbl_data.code_team t7 on t1.home_team=t7.team_code and t1.season_code=t7.season_code inner join KCC_EGIS.kbl_data.code_team t8 on t1.away_team=t8.team_code and t1.season_code=t8.season_code left outer join KCC_EGIS.kbl_data.team_daily_list t3 on t1.away_team=t3.team_code and t1.season_code=t3.season_code and t1.game_no=t3.game_no and t1.game_code=t3.game_code left outer join KCC_EGIS.kbl_data.team_daily_list t4 on t1.home_team=t4.team_code and t1.season_code=t4.season_code and t1.game_no=t4.game_no and t1.game_code=t4.game_code left outer join KCC_EGIS.kbl_data.game_info t5 on t1.season_code=t5.season_code and t1.game_no=t5.game_no and t1.game_code=t5.game_code left outer join KCC_EGIS.kbl_data.team_schedule_onair t6 on t1.season_code=t6.season_code and t1.game_code=t6.game_code and t1.game_no=t6.game_no where t1.season_code='45' and (t1.home_team= 60 or t1.away_team= 60)
and t1.game_round = #{game_round}
and t1.game_code = #{game_code}
order by t1.game_date desc

select count(*) from (select a.* from KCC_EGIS.egis.TBL_NEWS a
where 1=1 
and a.part = 'news'
and flag = 'N'
and linkurl != ''
and linkurl LIKE '%https://%'  
and LEN(linkurl) >= 8


		and (a.subject like CONCAT('%', #{keyWord}, '%') or a.keyword_tag like CONCAT('%', #{keyWord}, '%'))




		and a.game_date = #{game}




		and a.tags like CONCAT('%', #{player}, '%')




		and a.game_date BETWEEN #{first_game_date} AND #{last_game_date}




	and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}


) A

SELECT * FROM ( select a.* from KCC_EGIS.egis.TBL_NEWS a where 1=1 and a.part = 'news' and flag = 'N' and linkurl != '' and linkurl LIKE '%https://%' and LEN(linkurl) >= 8
and (a.subject like CONCAT('%', #{keyWord}, '%') or a.keyword_tag like CONCAT('%', #{keyWord}, '%'))
and a.game_date = #{game}
and a.tags like CONCAT('%', #{player}, '%')
and a.game_date BETWEEN #{first_game_date} AND #{last_game_date}
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
) A ORDER BY num desc OFFSET (#{currentPage}-1)*#{limit} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT * 
	FROM (	
					select a.* from KCC_EGIS.egis.TBL_NEWS a 
			where 1=1 
			and a.part = 'movie'
			and flag = 'N'


					and a.etc1 = #{etc1}




					and (a.subject like CONCAT('%', #{keyWord}, '%') or a.keyword_tag like CONCAT('%', #{keyWord}, '%'))




					and a.game_date = #{game}




					and a.tags like CONCAT('%', #{player}, '%')




					and a.game_date BETWEEN #{first_game_date} AND #{last_game_date}




					and a.wtype = #{wtype}




				and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}


			) A 

		ORDER BY num desc


		ORDER BY ${otype} DESC

	OFFSET (#{currentPage}-1)*#{limit} ROWS
	FETCH NEXT #{limit} ROWS ONLY

select count(*) from (select a.* from KCC_EGIS.egis.TBL_NEWS a 
where 1=1 
and a.part = 'movie'
and flag = 'N'


		and a.etc1 = #{etc1}




		and (a.subject like CONCAT('%', #{keyWord}, '%') or a.keyword_tag like CONCAT('%', #{keyWord}, '%'))




		and a.game_date = #{game}




		and a.tags like CONCAT('%', #{player}, '%')




		and a.game_date BETWEEN #{first_game_date} AND #{last_game_date}




		and a.wtype = #{wtype}




	and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}


) A

select top 1 * from KCC_EGIS.egis.TBL_NEWS
where part = #{part}
and game_date = #{game_date}
and etc1 = 'H'

SELECT * FROM ( SELECT a.*, (SELECT COUNT(*) FROM KCC_EGIS.egis.TBL_NEWS_PHOTO b WHERE b.info_num = a.num) AS photo_count FROM KCC_EGIS.egis.TBL_NEWS a WHERE 1=1 and flag = 'N'
and a.etc1 = #{etc1}
and a.part = #{part}
and (a.subject like CONCAT('%', #{keyWord}, '%') or a.keyword_tag like CONCAT('%', #{keyWord}, '%'))
and a.game_date = #{game}
and a.num in (select info_num from KCC_EGIS.egis.TBL_NEWS_PHOTO where tagcode like CONCAT('%', #{player}, '%'))
and a.game_date BETWEEN #{first_game_date} AND #{last_game_date}
and a.wtype = #{wtype}
and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}
) A
ORDER BY num desc
ORDER BY ${otype} DESC
OFFSET (#{currentPage}-1)*#{limit} ROWS FETCH NEXT #{limit} ROWS ONLY

select count(*) from (
SELECT 
    a.*, 
    (SELECT COUNT(*)
     FROM KCC_EGIS.egis.TBL_NEWS_PHOTO b
     WHERE b.info_num = a.num) AS photo_count
FROM 
    KCC_EGIS.egis.TBL_NEWS a
WHERE 1=1
and flag = 'N'


		and a.etc1 = #{etc1}



    	and a.part = #{part}



		and (a.subject like CONCAT('%', #{keyWord}, '%') or a.keyword_tag like CONCAT('%', #{keyWord}, '%'))




		and a.game_date = #{game}




		and a.num in (select info_num from KCC_EGIS.egis.TBL_NEWS_PHOTO where tagcode like CONCAT('%', #{player}, '%'))




		and a.game_date BETWEEN #{first_game_date} AND #{last_game_date}




		and a.wtype = #{wtype}




	and CONVERT(char(10),a.wdate,23) BETWEEN #{sdate} AND #{edate}


) A

select top 10 * from KCC_EGIS.egis.TBL_NEWS
where part = #{part}  
and etc1 = #{etc1}
and flag = 'N'
order by num desc

select TOP 1 *,FORMAT(wdate, 'yyyy.MM.dd') AS reg_date from KCC_EGIS.egis.TBL_NEWS
where game_date = #{game_date}
and part = #{part}

select count(*) from (
	select *,FORMAT(wdate , 'yyyy.MM.dd') AS formatted_date,
    FORMAT(wdate, 'HH:mm') AS formatted_time 
    from KCC_EGIS.egis.TBL_NEWS_TAIL
	where info_num = #{num}
) A

mapper.insert:
insert into KCC_EGIS.egis.TBL_NEWS_TAIL (
	 info_num, id, writer, content, ip, wdate, part 
)values(
	 #{num}, #{id}, #{name}, #{content}, #{ip}, getdate(), #{part}
)
