mapper.select:
    SELECT * FROM (
    SELECT TOP 1 
        A.season_code, A.game_code, A.game_no, A.team_code, B.kname, A.back_num, A.player_no, SUBSTRING(A.pos,1,1) pos,
        A.start_flag, ISNULL(A.play_min, 0) play_min, ISNULL(A.play_sec, 0) play_sec, A.fg, A.fg_a, A.ft, A.ft_a,
        A.threep, A.threep_a, A.dk, A.dk_a, A.pp, A.pp_a, A.o_r, A.d_r, (A.o_r + A.d_r) r_tot, A.a_s, A.s_t, A.b_s,
        A.gd, A.t_o, A.wft, A.woft, A.idf, A.tf, A.ef, A.foul_tot, A.fb, A.p_score, A.score, A.inout, A.inout1, A.fo
    FROM KCC_EGIS.kbl_data.player_daily_list A
    JOIN KCC_EGIS.kbl_data.player B ON A.season_code = B.season_code AND A.player_no = B.player_no
    WHERE A.season_code = #{season_code}
      AND A.game_code = #{game_code}
      AND A.game_no = #{game_no}
      AND A.team_code = #{team_code}
      AND NULLIF(A.play_min, '') IS NOT NULL
      AND NULLIF(A.play_sec, '') IS NOT NULL 
    ORDER BY A.score DESC, A.play_min, A.play_sec
) AS score
UNION ALL
SELECT * FROM (
    SELECT TOP 1 
        A.season_code, A.game_code, A.game_no, A.team_code, B.kname, A.back_num, A.player_no, SUBSTRING(A.pos,1,1) pos,
        A.start_flag, ISNULL(A.play_min, 0) play_min, ISNULL(A.play_sec, 0) play_sec, A.fg, A.fg_a, A.ft, A.ft_a,
        A.threep, A.threep_a, A.dk, A.dk_a, A.pp, A.pp_a, A.o_r, A.d_r, (A.o_r + A.d_r) r_tot, A.a_s, A.s_t, A.b_s,
        A.gd, A.t_o, A.wft, A.woft, A.idf, A.tf, A.ef, A.foul_tot, A.fb, A.p_score, A.score, A.inout, A.inout1, A.fo
    FROM KCC_EGIS.kbl_data.player_daily_list A
    JOIN KCC_EGIS.kbl_data.player B ON A.season_code = B.season_code AND A.player_no = B.player_no
    WHERE A.season_code = #{season_code}
      AND A.game_code = #{game_code}
      AND A.game_no = #{game_no}
      AND A.team_code = #{team_code}
      AND NULLIF(A.play_min, '') IS NOT NULL
      AND NULLIF(A.play_sec, '') IS NOT NULL 
    ORDER BY r_tot DESC, A.play_min, A.play_sec
) AS rebound
UNION ALL
SELECT * FROM (
    SELECT TOP 1 
        A.season_code, A.game_code, A.game_no, A.team_code, B.kname, A.back_num, A.player_no, SUBSTRING(A.pos,1,1) pos,
        A.start_flag, ISNULL(A.play_min, 0) play_min, ISNULL(A.play_sec, 0) play_sec, A.fg, A.fg_a, A.ft, A.ft_a,
        A.threep, A.threep_a, A.dk, A.dk_a, A.pp, A.pp_a, A.o_r, A.d_r, (A.o_r + A.d_r) r_tot, A.a_s, A.s_t, A.b_s,
        A.gd, A.t_o, A.wft, A.woft, A.idf, A.tf, A.ef, A.foul_tot, A.fb, A.p_score, A.score, A.inout, A.inout1, A.fo
    FROM KCC_EGIS.kbl_data.player_daily_list A
    JOIN KCC_EGIS.kbl_data.player B ON A.season_code = B.season_code AND A.player_no = B.player_no
    WHERE A.season_code = #{season_code}
      AND A.game_code = #{game_code}
      AND A.game_no = #{game_no}
      AND A.team_code = #{team_code}
      AND NULLIF(A.play_min, '') IS NOT NULL
      AND NULLIF(A.play_sec, '') IS NOT NULL 
    ORDER BY A.a_s DESC, A.play_min, A.play_sec
) AS assist

select b.kname AS pl_name ,b.back_num AS pl_back ,a.player_no ,a.season_code ,a.game_code ,a.game_count ,a.play_min ,a.play_sec ,ISNULL(ROUND((convert(float,fg)/nullif(convert(float,game_count), 0)), 1),0.0) AS fg ,ISNULL(ROUND((convert(float,fg_a)/nullif(convert(float,game_count), 0)), 1),0.0) AS fg_a ,ISNULL(ROUND((convert(float, fg) / NULLIF(convert(float, fg_a), 0) * 100),1),0.0) AS fgp ,ISNULL(ROUND((convert(float,ft)/nullif(convert(float,game_count), 0)), 1),0.0) AS ft ,ISNULL(ROUND((convert(float,ft_a)/nullif(convert(float,game_count), 0)), 1),0.0) AS ft_a ,ISNULL(ROUND((convert(float, ft) / NULLIF(convert(float, ft_a), 0) * 100),1),0.0) AS ftp ,ISNULL(ROUND((convert(float,threep)/nullif(convert(float,game_count), 0)), 1),0.0) AS threep ,ISNULL(ROUND((convert(float,threep_a)/nullif(convert(float,game_count), 0)), 1),0.0) AS threep_a ,ISNULL(ROUND((convert(float, threep) / NULLIF(convert(float, threep_a), 0) * 100),1),0.0) AS threepp ,ISNULL(ROUND((convert(float,pp)/nullif(convert(float,game_count), 0)), 1),0.0) AS pp ,ISNULL(ROUND((convert(float,pp_a)/nullif(convert(float,game_count), 0)), 1),0.0) AS pp_a ,ISNULL(ROUND((convert(float, pp) / NULLIF(convert(float, pp_a), 0) * 100),1),0.0) AS ppp ,ISNULL(ROUND((convert(float,o_r)/nullif(convert(float,game_count), 0)), 1),0.0) AS o_r ,ISNULL(ROUND((convert(float,d_r)/nullif(convert(float,game_count), 0)), 1),0.0) AS d_r ,ISNULL(ROUND((convert(float,o_r + d_r)/nullif(convert(float,game_count), 0)), 1),0.0) AS r_tot ,ISNULL(ROUND((convert(float,a_s)/nullif(convert(float,game_count), 0)), 1),0.0) AS a_s ,ISNULL(ROUND((convert(float,b_s)/nullif(convert(float,game_count), 0)), 1),0.0) AS b_s ,ISNULL(ROUND((convert(float,t_o)/nullif(convert(float,game_count), 0)), 1),0.0) AS t_o ,ISNULL(ROUND((convert(float,foul_tot)/nullif(convert(float,game_count), 0)), 1),0.0) AS foul_tot ,ISNULL(ROUND((convert(float,score)/nullif(convert(float,game_count), 0)), 1),0.0) AS pts from KCC_EGIS.kbl_data.player_sum a,KCC_EGIS.kbl_data.player b where quarter_gu ='XX' and a.game_code =#{game_code} and a.season_code =#{season_code} and a.season_code = b.season_code and a.player_no = b.player_no and b.team_code = '60' and b.pos in ('FD','C','GD')
ORDER BY CAST(b.back_num AS INT)
ORDER BY ${str} ${sort}

select b.kname AS pl_name ,b.back_num AS pl_back ,a.* ,ISNULL(ROUND((convert(float, fg) / NULLIF(convert(float, fg_a), 0) * 100),1),0.0) AS fgp ,ISNULL(ROUND((convert(float, threep) / NULLIF(convert(float, threep_a), 0) * 100),1),0.0) AS threepp ,ISNULL(ROUND((convert(float, pp) / NULLIF(convert(float, pp_a), 0) * 100),1),0.0) AS ppp ,(o_r + d_r) r_tot ,ISNULL(ROUND((convert(float, ft) / NULLIF(convert(float, ft_a), 0) * 100),1),0.0) AS ftp ,score AS pts from KCC_EGIS.kbl_data.player_sum a,KCC_EGIS.kbl_data.player b where quarter_gu ='XX' and a.game_code =#{game_code} and a.season_code =#{season_code} and a.season_code = b.season_code and a.player_no = b.player_no and b.team_code = '60' and b.pos in ('FD','C','GD')
ORDER BY CAST(b.back_num AS INT)
ORDER BY ${str} ${sort}

select * from KCC_EGIS.egis.TBL_LIKE_PLAYER_PHOTO
where
id = #{id}
and player_no = #{player_no}

mapper.delete:
delete from KCC_EGIS.egis.TBL_LIKE_PLAYER_PHOTO
where player_no = #{player_no}
and id = #{id}
