mapper.select:
SELECT * FROM ( select t1.season_code, t1.game_no, t1.game_code,t1.game_round, t1.away_team, t1.home_team, t1.game_date, SUBSTRING(t1.game_date, 1, 4) + '.' +SUBSTRING(t1.game_date, 5, 2) + '.' + SUBSTRING(t1.game_date, 7, 2) AS game_date_format, SUBSTRING(t1.game_date,0,5) AS game_date_yy, SUBSTRING(t1.game_date,5,2) AS game_date_mm, SUBSTRING(t1.game_date,7,2) AS game_date_dd, CONVERT(DATETIME, t1.game_date +' '+ SUBSTRING(t1.game_start, 1, 2) + ':' + SUBSTRING(t1.game_start, 3, 2), 112) game_date_all, isnull(t3.play_min, 0) play_min, t2.stadium_name_2, isnull(t3.score, 0) away_score, isnull(t4.score, 0) home_score, isnull(t5.game_end, '') game_end, t1.game_start, SUBSTRING(t1.game_start, 1, 2) + ':' + SUBSTRING(t1.game_start, 3, 2) AS game_start_format, t1.week_day, t6.tv_play, t7.team_name_1 AS home_team_name,t8.team_name_1 AS away_team_name from kcc2022_esoom.kbl_data.team_schedule t1 inner join kcc2022_esoom.kbl_data.code_stadium t2 on t1.stadium_code=t2.stadium_code and t1.season_code=t2.season_code inner join kcc2022_esoom.kbl_data.code_team t7 on t1.home_team=t7.team_code and t1.season_code=t7.season_code inner join kcc2022_esoom.kbl_data.code_team t8 on t1.away_team=t8.team_code and t1.season_code=t8.season_code left outer join kcc2022_esoom.kbl_data.team_daily_list t3 on t1.away_team=t3.team_code and t1.season_code=t3.season_code and t1.game_no=t3.game_no and t1.game_code=t3.game_code left outer join kcc2022_esoom.kbl_data.team_daily_list t4 on t1.home_team=t4.team_code and t1.season_code=t4.season_code and t1.game_no=t4.game_no and t1.game_code=t4.game_code left outer join kcc2022_esoom.kbl_data.game_info t5 on t1.season_code=t5.season_code and t1.game_no=t5.game_no and t1.game_code=t5.game_code left outer join kcc2022_esoom.kbl_data.team_schedule_onair t6 on t1.season_code=t6.season_code and t1.game_code=t6.game_code and t1.game_no=t6.game_no where 1=1
and t1.season_code = #{season_code}
and t1.game_code = #{game_code}
and t1.game_date like CONCAT('%', #{season_month}, '%')
and t1.game_round = #{round}
and (t1.home_team= 60 or t1.away_team= 60)
and t1.home_team= 60
and t1.away_team= 60
) A ORDER BY game_date OFFSET (#{currentPage}-1)*#{limit} ROWS FETCH NEXT #{limit} ROWS ONLY

select count(*) from ( select t1.season_code, t1.game_no, t1.game_code,t1.game_round, t1.away_team, t1.home_team, t1.game_date, SUBSTRING(t1.game_date, 1, 4) + '.' +SUBSTRING(t1.game_date, 5, 2) + '.' + SUBSTRING(t1.game_date, 7, 2) AS game_date_format, SUBSTRING(t1.game_date,0,5) AS game_date_yy, SUBSTRING(t1.game_date,5,2) AS game_date_mm, SUBSTRING(t1.game_date,7,2) AS game_date_dd, CONVERT(DATETIME, t1.game_date +' '+ SUBSTRING(t1.game_start, 1, 2) + ':' + SUBSTRING(t1.game_start, 3, 2), 112) game_date_all, isnull(t3.play_min, 0) play_min, t2.stadium_name_2, isnull(t3.score, 0) away_score, isnull(t4.score, 0) home_score, isnull(t5.game_end, '') game_end, t1.game_start, SUBSTRING(t1.game_start, 1, 2) + ':' + SUBSTRING(t1.game_start, 3, 2) AS game_start_format, t1.week_day, t6.tv_play, t7.team_name_1 AS home_team_name,t8.team_name_1 AS away_team_name from kcc2022_esoom.kbl_data.team_schedule t1 inner join kcc2022_esoom.kbl_data.code_stadium t2 on t1.stadium_code=t2.stadium_code and t1.season_code=t2.season_code inner join kcc2022_esoom.kbl_data.code_team t7 on t1.home_team=t7.team_code and t1.season_code=t7.season_code inner join kcc2022_esoom.kbl_data.code_team t8 on t1.away_team=t8.team_code and t1.season_code=t8.season_code left outer join kcc2022_esoom.kbl_data.team_daily_list t3 on t1.away_team=t3.team_code and t1.season_code=t3.season_code and t1.game_no=t3.game_no and t1.game_code=t3.game_code left outer join kcc2022_esoom.kbl_data.team_daily_list t4 on t1.home_team=t4.team_code and t1.season_code=t4.season_code and t1.game_no=t4.game_no and t1.game_code=t4.game_code left outer join kcc2022_esoom.kbl_data.game_info t5 on t1.season_code=t5.season_code and t1.game_no=t5.game_no and t1.game_code=t5.game_code left outer join kcc2022_esoom.kbl_data.team_schedule_onair t6 on t1.season_code=t6.season_code and t1.game_code=t6.game_code and t1.game_no=t6.game_no where 1=1
and t1.season_code = #{season_code}
and t1.game_code = #{game_code}
and t1.game_date like CONCAT('%', #{season_month}, '%')
and t1.game_round = #{round}
and (t1.home_team= 60 or t1.away_team= 60)
and t1.home_team= 60
and t1.away_team= 60
) A

select count(*) from kcc2022_esoom.kbl_data.team_daily_list
where 1=1

and season_code = ${season_code}

and team_code = ${team_code} 
and away_team = ${away_code}

	and win = 1


	and loss = 1

select * from kcc2022_esoom.kbl_data.team_quarter_list
where
season_code =   #{season_code}
and game_code = #{game_code}
and game_no   = #{game_no}

select *, ROUND((convert(float,fg)/convert(float,fg_a) * 100), 1) AS fgp,
ROUND((convert(float,ft)/convert(float,ft_a) * 100), 1) AS ftp,
ROUND((convert(float,threep)/convert(float,threep_a) * 100), 1) AS threepp,
(o_r + d_r) r_tot 
from kcc2022_esoom.kbl_data.team_daily_list
where
season_code =   #{season_code}
and game_code = #{game_code}
and game_no   = #{game_no}
and team_code   = #{team_code}

select a.away_team,ISNULL(sum(CONVERT(int, a.win)), 0) win, ISNULL(sum(CONVERT(int, a.loss)), 0) loss from kcc2022_esoom.kbl_data.team_daily_list a WHERE a.season_code ='43' and a.team_code ='60' and a.game_code ='01'
and a.away_team = #{team_code}
GROUP BY a.away_team

select a.away_team,ISNULL(sum(CONVERT(int, a.win)), 0) win, ISNULL(sum(CONVERT(int, a.loss)), 0) loss from kcc2022_esoom.kbl_data.team_daily_list a WHERE a.season_code ='41' and a.team_code ='60' and a.game_code ='01'
and a.away_team = #{team_code}
GROUP BY a.away_team

select a.away_team,ISNULL(sum(CONVERT(int, a.win)), 0) win, ISNULL(sum(CONVERT(int, a.loss)), 0) loss from kcc2022_esoom.kbl_data.team_daily_list a WHERE a.team_code ='60' and a.game_code ='01'
and a.away_team = #{team_code}
GROUP BY a.away_team
